definition.script:
pipeline {
    agent any
    options {
        checkoutToSubdirectory('usas')
    }
    environment {
        MACHINE = "${MACHINE}"
        PROJ_CREATE = "${PROJ_CREATE}"
        IP_SYNTH = "${IP_SYNTH}"
        PROJ_SYNTH = "${PROJ_SYNTH}"
        OPT_DESIGN = "${OPT_DESIGN}"
        PLACE_DESIGN = "${PLACE_DESIGN}"
        PHYS_SYNTH = "${PHYS_SYNTH}"
        ROUTE_DESIGN = "${ROUTE_DESIGN}"
        BIT_GEN = "${BIT_GEN}"
    }

    stages {
        stage('Setup') {
            steps {
                echo 'Cloning usas...'
                git(url: 'https://github.ms.northgrum.com/undersea/usas.git', branch: 'build_from_scripts')
            }
        }   
        stage('Project Creation') {
            steps {
                echo 'Project Creation...'
                sh "./jenkins_scripts/proj_create.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('IP Synthesis') {
            steps {
                echo 'IP Synthesis...'
                sh "./jenkins_scripts/ip_synth.sh ${MACHINE} ${IP_SYNTH}"
            }
        }
        stage('Project Synthesis') {
            steps {
                echo 'Project Synthesis...'
                sh "./jenkins_scripts/proj_synth.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('Opt Design') {
            steps {
                echo 'Opt Design...'
                sh "./jenkins_scripts/opt_design.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('Place Design') {
            steps {
                echo 'Place Design...'
                sh "./jenkins_scripts/place_design.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('Physical Synthesis') {
            steps {
                echo 'Physical Synthesis...'
                sh "./jenkins_scripts/phys_synth.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('Route Design') {
            steps {
                echo 'Route Design...'
                sh "./jenkins_scripts/route_design.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
        stage('Bitstream Generation') {
            steps {
                echo 'Bitstream Generation...'
                sh "./jenkins_scripts/bit_gen.sh ${MACHINE} ${PROJ_CREATE}"
            }
        }
    }
    post {
        always {
            echo "Hello"
            step([$class: 'LogParserPublisher', parsingRulesPath: '/home/jenkins/parsing_rules/fw_parsing', userProjectRule: false, failBuildOnError: true])
        }
    }
}
